mapper.select:
SELECT id
FROM user
WHERE tel = #{tel}

SELECT *
FROM orders
WHERE user_id = #{userId}
  AND order_id = #{orderId}

mapper.update:
UPDATE user
SET password = #{encodedPassword}
WHERE id = #{userId}

mapper.delete:
DELETE
FROM sns_user
WHERE user_id = #{userId}
  AND client_name = #{clientName}
