mapper.select:
SELECT id
FROM user
WHERE tel = #{tel}

SELECT o.*, od.order_detail_id, od.product_id, od.product_price, od.quantity, p.name as product_name, p.image_data as product_image, ##반품한게 있는지 확인 (SELECT COUNT(*) > 0 FROM order_detail od2 INNER JOIN refunds rf ON od2.order_detail_id = rf.order_detail_id WHERE od2.order_id = o.order_id) as hasAnyRefundedItems, ##리뷰한게 있는지 확인 (SELECT COUNT(*) > 0 FROM review rv WHERE rv.order_id = o.order_id) as hasAnyReviewedItems, ##모든 상품들을 리뷰 했는지 확인 ##주문상세내역 상품 수를 확인한 다음, 리뷰 종류 갯수를 세서 동일한지? CASE WHEN (SELECT COUNT(*) FROM order_detail od_total WHERE od_total.order_id = o.order_id) = (SELECT COUNT(DISTINCT product_id) FROM review rv WHERE rv.order_id = o.order_id) THEN TRUE ELSE FALSE END AS hasAllReviewedItems FROM ( SELECT order_id FROM orders WHERE user_id = #{userId}
##order_date가 크거나 같다(이상) AND order_date >= #{pagenation.startDate}
##order_date가 작거나 같다(이하) AND order_date < DATE_ADD(#{pagenation.endDate}, INTERVAL 1 DAY)
AND order_status = #{pagenation.status}
ORDER BY order_date DESC LIMIT #{pagenation.size} OFFSET #{pagenation.offset} ) AS paged_orders INNER JOIN orders o ON paged_orders.order_id = o.order_id LEFT JOIN order_detail od ON o.order_id = od.order_id LEFT JOIN product p ON od.product_id = p.product_id ORDER BY o.order_date DESC

SELECT count(*)
FROM orders
WHERE user_id = #{userId}

    ##order_date가 크거나 같다(이상)
    AND order_date >= #{pagenation.startDate}


    ##order_date가 작거나 같다(이하)
    AND order_date < DATE_ADD(#{pagenation.endDate}, INTERVAL 1 DAY)


    AND order_status = #{pagenation.status}

mapper.update:
UPDATE orders
SET order_status = #{newStatus}
WHERE order_id = #{orderId}
  AND user_id = #{userId}

mapper.select:
SELECT o.order_id
FROM order_detail od
         INNER JOIN orders o ON od.order_id = o.order_id
WHERE od.order_detail_id = #{orderDetailId}
  AND o.user_id = #{userId}

mapper.update:
UPDATE user
SET password = #{encodedPassword}
WHERE id = #{userId}

mapper.delete:
DELETE
FROM sns_user
WHERE user_id = #{userId}
  AND client_name = #{clientName}
